package variableAndMethods;

public class Address03 {

		// TODO Auto-generated method stub
		//class body 
		//CLASS BODY CONTAINS
		
		// variables - declared/initialize
		//constructor - declared/initialize 
		//method - implemented/initialize 
		//
		static String Name = "Shama"; //String inside double quotation
		static int age = 44; //int without any quotation
		static char sex = 'F'; //char with single quotation 
		static double grade = 3.876; //double no quotation 
		
		public static void main(String[] args) {
		System.out.println(Name); //a non static variable can not initialize under a static method
		System.out.println(age);
		System.out.println(sex);
		System.out.println(grade);
		

	}//closing of class body 

} 
